
public class Calculations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String abc="This is a String";
		System.out.println(abc);
		
		//data type=int
		//variable=d
		//value=10
		
		int a=5;
		int b=10;
		int d=10;
		
		//Below line is for Addition
		int c=a+b+d;
		
		//Bellow line is for Subtraction
		int e=b-a;
		
		//Below line is for Multiplication
		int f=d*b;
		
		//Below line is for Division
		int g=d/a;
		
		//Modulus
		int i=a%2;
	
		//concatenation
		System.out.println("This is addition of line a,b and d:"+c);
		System.out.println("This is subtraction of line b and a:"+e);
		System.out.println("This is multiplication of line d and b:"+f);
		System.out.println("This is division of line d and a:"+g);
		System.out.println(i);
		
		
		
		

	}

}
